def preprocess_image(image):
    try:
        # Keep original image for display
        display_image = image.copy()

        # Process image for model
        image = image.convert('RGB')
        image = image.resize((224, 224))
        image = np.array(image)
        image = image / 255.0
        image = np.expand_dims(image, axis=0)
        return image, display_image
    except Exception as e:
        st.error(f"á€“á€¬á€á€ºá€•á€¯á€¶á€¡á€á€»á€€á€ºá€¡á€œá€€á€ºá€™á€»á€¬á€¸ á€•á€¼á€„á€ºá€†á€„á€ºá€›á€¬á€á€½á€„á€º á€¡á€™á€¾á€¬á€¸á€á€…á€ºá€á€¯á€–á€¼á€…á€ºá€•á€±á€«á€ºá€á€²á€·á€á€Šá€º: {str(e)}")
        return None, None


def is_skin_image(image):
    """Check if the uploaded image appears to be a skin image"""
    try:
        # Convert to numpy array
        img_array = np.array(image)

        # Check if image has 3 channels (RGB)
        if len(img_array.shape) != 3 or img_array.shape[2] != 3:
            return False

        # Convert to HSV color space
        hsv = cv2.cvtColor(img_array, cv2.COLOR_RGB2HSV)

        # Define skin color range in HSV
        lower_skin = np.array([0, 48, 80], dtype=np.uint8)
        upper_skin = np.array([20, 255, 255], dtype=np.uint8)

        # Create mask for skin colors
        mask = cv2.inRange(hsv, lower_skin, upper_skin)

        # Calculate percentage of skin-colored pixels
        skin_pixels = np.count_nonzero(mask)
        total_pixels = mask.shape[0] * mask.shape[1]
        skin_percentage = (skin_pixels / total_pixels) * 100

        # Consider it a skin image if at least 10% of pixels are skin-colored
        return skin_percentage > 10
    except Exception as e:
        st.error(f"á€¡á€›á€±á€•á€¼á€¬á€¸á€•á€¯á€¶á€…á€…á€ºá€†á€±á€¸á€›á€¬á€á€½á€„á€º á€¡á€™á€¾á€¬á€¸á€á€…á€ºá€á€¯á€–á€¼á€…á€ºá€•á€±á€«á€ºá€á€²á€·á€á€Šá€º: {str(e)}")
        return False


